import messages from '../components/messagesData'; // Import the messages
const friends = [
    { id: 1, name: 'John Doe', lastMessage: 'Struggling with JavaScript basics...', time: '10:30 AM', avatar: 'https://th.bing.com/th/id/OIP.O8vv9O4Ku4HvFQyep-NXMAHaLG?w=183&h=274&c=7&pcl=1b1a19&r=0&o=5&pid=1.7' },
    { id: 2, name: 'Jane Smith', lastMessage: 'Any tips for Python courses?', time: '09:45 AM', avatar: 'https://th.bing.com/th/id/OIP.AwGBn0RaiFXEpXemdj-2LQHaLG?w=183&h=274&c=7&pcl=1b1a19&r=0&o=5&pid=1.7' },
    { id: 3, name: 'Chris Brown', lastMessage: 'Best React tutorials?', time: '08:15 AM', avatar: 'data:image/jpeg;base64,' },
    { id: 4, name: 'Lisa Johnson', lastMessage: 'Howâ€™s the web dev course?', time: '07:50 AM', avatar: 'data:image/jpeg;base64,' },
    { id: 5, name: 'Mike Lee', lastMessage: 'Finished my Django project!', time: '06:30 AM', avatar: 'data:image/jpeg;base64,' },
    { id: 6, name: 'Emily Davis', lastMessage: 'Issues with database setup...', time: '06:00 AM', avatar: 'data:image/jpeg;base64,' },
    { id: 7, name: 'David Wilson', lastMessage: 'Can we discuss CSS grids?', time: '05:45 AM', avatar: 'data:image/jpeg;base64,' },
    { id: 8, name: 'Sophia Turner', lastMessage: 'Loving the TailwindCSS course!', time: '05:00 AM', avatar: 'https://th.bing.com/th/id/OIP.xfJQTk0Qfej0wsNMJCIPXAHaEK?w=183&h=103&c=7&pcl=1b1a19&r=0&o=5&pid=1.7' },
    { id: 9, name: 'Oliver Martinez', lastMessage: 'Any good Java tutorials?', time: '04:30 AM', avatar: 'https://th.bing.com/th?id=OIF.WWdVIYLznxCciPvSAriB%2fw&w=183&h=325&c=7&pcl=1b1a19&r=0&o=5&pid=1.7' },
    { id: 10, name: 'Emma Garcia', lastMessage: 'Best practices for APIs?', time: '04:00 AM', avatar: 'https://th.bing.com/th/id/OIP.sDaiPcWpDMF7Uj38VvXjPwHaEo?w=183&h=114&c=7&pcl=1b1a19&r=0&o=5&pid=1.7' },
    { id: 11, name: 'Lucas Robinson', lastMessage: 'Debugging issues in PHP...', time: '03:45 AM', avatar: 'data:image/jpeg;base64,' },
    { id: 12, name: 'Mia Walker', lastMessage: 'Struggling with Node.js...', time: '03:15 AM', avatar: 'data:image/jpeg;base64,' },
  ];
const MessagePage = () => {
  const { id } = useParams();
  const chatMessages = messages[id] || [];
  // Get the user based on the current `id` from the URL
const friend = friends.find(user => user.id === parseInt(id)); // id is from `useParams()`
const friendAvatar = friend ? friend.avatar : 'https://via.placeholder.com/50';
const friendName = friend ? friend.name : 'Unknown';
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      // Add the new message to the chat
      chatMessages.push({
        sender: 'You',
        text: newMessage,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      });
      setNewMessage('');
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen flex flex-col">
      {/* Header with friend's avatar and name */}
      <header className="bg-blue-600 text-white py-4 px-6 w-full flex items-center">
        <Link to="/chats">
          <IoArrowBack size={25} className="text-white" />
        </Link>
        <img src={friendAvatar} alt="friend-avatar" className="rounded-full w-10 h-10 ml-6 object-cover" />
        <h1 className="text-2xl font-bold ml-3">{friendName}</h1>
      </header>

      {/* Messages Section */}
      <main className="flex-1 p-4 overflow-auto">
        <div className="space-y-4">
          {chatMessages.map((msg, index) => (
            <div
              key={index}
              className={`flex ${msg.sender === 'You' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`${
                  msg.sender === 'You' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'
                } p-3 rounded-lg max-w-sm`}
              >
                <p>{msg.text}</p>
                <span className="block text-xs text-gray-500 mt-1">{msg.time}</span>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* Input Section for New Message */}
      <footer className="bg-white py-2 px-4 flex items-center border-t border-gray-300">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 p-2 rounded-lg border border-gray-300"
        />
        <button
          onClick={handleSendMessage}
          className="ml-2 p-2 bg-blue-600 rounded-full text-white"
        >
          <IoSend size={20} />
        </button>
      </footer>
    </div>
  );
};

export default MessagePage;
